import { Component } from '@angular/core';

@Component({
  selector: 'app-todo',
  templateUrl: './todo.component.html',
  styleUrls: ['./todo.component.scss'],
})
export class TodoComponent {
  tasks = [
    {
      id: 1,
      text: 'task 1',
      complete: false,
    },
    {
      id: 2,
      text: 'task 2',
      complete: true,
    },
    {
      id: 3,
      text: 'task 2',
      complete: true,
    },
  ];
  todoText = '';
  constructor() {
    setTimeout(() => {
      this.tasks[0].complete = true;
    }, 3000);
  }
  addTodo() {
    this.tasks.push({ text: this.todoText, complete: false, id: 4 });
    this.todoText = '';
  }
}
