import { Component } from '@angular/core';

@Component({
  selector: 'app-todo',
  templateUrl: './todo.component.html',
  styleUrls: ['./todo.component.scss'],
})
export class TodoComponent {
  tasks = [
    {
      text: 'task 1',
      complete: false,
    },
    {
      text: 'task 2',
      complete: true,
    },
    {
      text: 'task 2',
      complete: true,
    },
  ];
  addTodo() {
    this.tasks.push({ text: 'task 4', complete: false });
  }
}
