import { Component } from '@angular/core';

@Component({
  selector: 'app-todo',
  templateUrl: './todo.component.html',
  styleUrls: ['./todo.component.scss'],
})
export class TodoComponent {
  tasks = [
    {
      id: 1,
      text: 'task 1',
      complete: false,
    },
    {
      id: 2,
      text: 'task 2',
      complete: true,
    },
    {
      id: 3,
      text: 'task 2',
      complete: true,
    },
  ];
  todoText = '';
  lastCount = this.tasks.length;
  constructor() {
    setTimeout(() => {
      this.tasks[0].complete = true;
    }, 3000);
  }
  toggleTask(id: number) {
    const findTask = this.tasks.find((t) => t.id === id);
    if (findTask) {
      findTask.complete = !findTask.complete;
    }
    console.log(findTask);
  }
  addTodo() {
    this.tasks.push({
      id: ++this.lastCount,
      text: this.todoText,
      complete: false,
    });
    this.todoText = '';
  }
  deleteTodo(id: number) {
    this.tasks = this.tasks.filter((t) => t.id !== id);
  }
  deleteTasks() {
    this.tasks = [];
  }
}
