import { Component } from '@angular/core';

@Component({
  selector: 'app-todo',
  templateUrl: './todo.component.html',
  styleUrls: ['./todo.component.scss'],
})
export class TodoComponent {
  tasks = [
    {
      id: 1,
      text: 'task 1',
      complete: false,
      edit: false,
    },
    {
      id: 2,
      text: 'task 2',
      complete: true,
      edit: false,
    },
    {
      id: 3,
      text: 'task 2',
      complete: true,
      edit: false,
    },
  ];
  todoText = '';
  lastCount = this.tasks.length;

  constructor(private tasksService: TasksService) {}
  toggleTask(id: number) {
    const findTask = this.tasks.find((t) => t.id === id);
    if (findTask) {
      findTask.complete = !findTask.complete;
    }
  }
  addTodo() {
    this.tasks.push({
      id: ++this.lastCount,
      text: this.todoText,
      complete: false,
      edit: false,
    });
    this.todoText = '';
  }
  deleteTodo(id: number) {
    this.tasks = this.tasks.filter((t) => t.id !== id);
  }
  deleteTasks() {
    this.tasks = [];
  }
  submitEdit(text: string, id: number) {
    const findTask = this.tasks.find((t) => t.id === id);
    if (findTask) {
      findTask.text = text;
      findTask.edit = false;
    }
  }
}
